configuration.config:
   type Loaders is ("RAM");

   Loader : Loaders := external ("LOADER", "RAM");

   package Compiler is
     Common_Required_Switches := ("-mlittle-endian", "-mhard-float",
        "-mcpu=cortex-a7", "-mfpu=vfpv4", "-marm", "-mno-unaligned-access");

     Asm_Required_Switches := ();

     for Leading_Required_Switches ("Ada") use
        Compiler'Leading_Required_Switches ("Ada") &
        Common_Required_Switches;

     for Leading_Required_Switches ("C") use
        Compiler'Leading_Required_Switches ("C") &
        Common_Required_Switches;

     for Leading_Required_Switches ("Asm") use
        Compiler'Leading_Required_Switches ("Asm") &
        Common_Required_Switches;

     for Leading_Required_Switches ("Asm2") use
        Compiler'Leading_Required_Switches ("Asm2") &
        Common_Required_Switches;

     for Leading_Required_Switches ("Asm_Cpp") use
        Compiler'Leading_Required_Switches ("Asm_Cpp") &
        Common_Required_Switches &
        Asm_Required_Switches;
   end Compiler;

   package Linker is
      --  Add -L for linker scripts, as some of them use INCLUDE.
      for Required_Switches use Linker'Required_Switches &
        ("-L${RUNTIME_DIR(ada)}/adalib", "-nolibc", "-nostartfiles") &
        Compiler.Common_Required_Switches;

      case Loader is
         when "RAM" =>
	    for Required_Switches use Linker'Required_Switches &
	       ("-Wl,-T${RUNTIME_DIR(ada)}/bsp/ram.ld");
       end case;
   end Linker;
