configuration.config:
   type Loaders is ("PROBE", "FLASH", "MONITOR", "HIRAM", "LORAM");

   Loader : Loaders := external ("LOADER", "PROBE");

   package Compiler is
     Common_Required_Switches := ("-mbig-endian", "-mhard-float",
        "-mcpu=cortex-r4", "-mfpu=vfpv3-d16", "-marm");

     Asm_Required_Switches := ();

     for Leading_Required_Switches ("Ada") use
        Compiler'Leading_Required_Switches ("Ada") &
        Common_Required_Switches;

     for Leading_Required_Switches ("C") use
        Compiler'Leading_Required_Switches ("C") &
        Common_Required_Switches;

     for Leading_Required_Switches ("Asm") use
        Compiler'Leading_Required_Switches ("Asm") &
        Common_Required_Switches;

     for Leading_Required_Switches ("Asm2") use
        Compiler'Leading_Required_Switches ("Asm2") &
        Common_Required_Switches;

     for Leading_Required_Switches ("Asm_Cpp") use
        Compiler'Leading_Required_Switches ("Asm_Cpp") &
        Common_Required_Switches &
        Asm_Required_Switches;
   end Compiler;

   package Linker is
      for Required_Switches use Linker'Required_Switches &
        ("-L${RUNTIME_DIR(ada)}/adalib", "-lgnat") &
        Compiler.Common_Required_Switches &
        ("-nostdlib", "-lgcc");

      case Loader is
         when "PROBE" =>
	    for Required_Switches use Linker'Required_Switches &
	       ("-T", "${RUNTIME_DIR(ada)}/arch/tms570.ld");
         when "FLASH" =>
	    for Required_Switches use Linker'Required_Switches &
	       ("-T", "${RUNTIME_DIR(ada)}/arch/flash.ld",
                "-Wl,-z,max-page-size=0x1000");
         when "MONITOR" =>
	    for Required_Switches use Linker'Required_Switches &
	       ("-T", "${RUNTIME_DIR(ada)}/arch/monitor.ld",
                "-Wl,-z,max-page-size=0x1000");
         when "HIRAM" =>
	    for Required_Switches use Linker'Required_Switches &
	       ("-T", "${RUNTIME_DIR(ada)}/arch/hiram.ld",
                "-Wl,-z,max-page-size=0x1000");
         when "LORAM" =>
	    for Required_Switches use Linker'Required_Switches &
	       ("-T", "${RUNTIME_DIR(ada)}/arch/loram.ld",
                "-Wl,-z,max-page-size=0x1000");
       end case;
   end Linker;

   package Binder is
      for Required_Switches ("Ada") use Binder'Required_Switches ("Ada")
        & ("-nostdlib");
   end Binder;
