configuration.config:
Desk_Home := external ("DESKHOME");

for Object_Dir use external("GPR_OBJ_DIR", ".");

package Builder is
   for Executable_Suffix use ".exe";
end Builder;

package Binder is
   for Bindfile_Option_Substitution ("-static") use ("");
end Binder;

package Compiler is
  Common_Required_Switches :=
    ("-fsjlj",
     "-mabi=aapcs-linux",
     "-march=armv7-a",
     "-mfloat-abi=hard",
     "-mfpu=vfpv3",
     "-mthumb",
     "-mthumb-interwork",
     "-mno-unaligned-access",
     "-mrestrict-it",
     "-fPIC");

  Asm_Required_Switches := ();

  for Leading_Required_Switches ("Ada") use
     Compiler'Leading_Required_Switches ("Ada") &
     Common_Required_Switches;

  for Leading_Required_Switches ("C") use
     Compiler'Leading_Required_Switches ("C") &
     Common_Required_Switches;

  for Leading_Required_Switches ("Asm") use
     Compiler'Leading_Required_Switches ("Asm") &
     Common_Required_Switches;

  for Leading_Required_Switches ("Asm2") use
     Compiler'Leading_Required_Switches ("Asm2") &
     Common_Required_Switches;

  for Leading_Required_Switches ("Asm_Cpp") use
     Compiler'Leading_Required_Switches ("Asm_Cpp") &
     Common_Required_Switches &
     Asm_Required_Switches;
end Compiler;

package Linker is
   for Driver use external("LINK_DRIVER", "");
   for Leading_Switches ("ada") use (external ("LINK_DEOS_PATH", ""), external ("GCC_DIR", ""), external ("TARGET_ARCH", ""), external ("IMAGE_TYPE", ""));
end Linker;
