configuration.config:
  package Compiler is
     Common_Required_Switches := ();

     for Leading_Required_Switches ("Ada") use
        ("-c", "-I${RUNTIME_DIR(ada)}/common", "--RTS=zfp") &
        Common_Required_Switches;

     for Leading_Required_Switches ("C") use
        Compiler'Leading_Required_Switches ("C") &
        Common_Required_Switches;
  end Compiler;

  package Linker is
      for Required_Switches use Linker'Required_Switches &
        ("${RUNTIME_DIR(ada)}/adalib/libgnat.a") &
        Compiler.Common_Required_Switches;
  end Linker;

   package Binder is
      for Required_Switches ("Ada") use
        ("gnatbind_prefix=lmp-elf-", "--RTS=zfp",
	 "-aO${RUNTIME_DIR(ada)}/adalib");
   end Binder;
