configuration.config:
package Compiler is
   Common_Required_Switches := ("-msoft-float", "-mno-sdata");
   --  Switch for all languages: do not use FPU

   for Leading_Required_Switches ("Ada") use
      Compiler'Leading_Required_Switches ("Ada") &
      Common_Required_Switches;

   for Leading_Required_Switches ("C") use
      Compiler'Leading_Required_Switches ("C") &
      Common_Required_Switches;

   for Leading_Required_Switches ("Asm_Cpp") use
      Compiler'Leading_Required_Switches ("Asm_Cpp") &
      Common_Required_Switches;
end Compiler;

LDSCRIPT := external ("LDSCRIPT", "${RUNTIME_DIR(ada)}/arch/ram.ld");
--  Linker script to use

package Linker is
    for Required_Switches use Linker'Required_Switches &
      ("-L${RUNTIME_DIR(ada)}/adalib") &
      Compiler.Common_Required_Switches &
      ("-Wl,-z,max-page-size=0x1000", "-Wl,-T" & LDSCRIPT);
    --  Specify the runtime library, the compiler switches and the
    --  linker script. We use a small max-page-size value to reduce the
    --  size of the executable.
end Linker;
